import React, {useState, useEffect, useRef} from 'react'

export default function IsIncorrect() {

    const [incorrectData, setIncorrectData] = useState([])
    const isMounted = useRef(false)


    async function getIncorrectRecipes(){
        try{
            const data = await fetch('https://beta.eagleowl.in/api/v1/mock/organization/18/outlet/18/recipe/recipes/?page=1&')
            const json = await data.json()
            setIncorrectData(json.results)
        }
        catch(err){
            console.log(err)
        }
        
    }
    
    useEffect(()=>{
        getIncorrectRecipes()
    },[])

    useEffect(()=>{
        if(isMounted.current){
            console.log(incorrectData)
        }
        else{
            isMounted.current = true
        }
    },[incorrectData])

    return(
        <>
            {
                (isMounted.current ? incorrectData.map( recipe => {
                    console.log(recipe)
                    return(
                            <div  key={recipe.id}>
                                <div>
                                    <p>{recipe.name}</p>
                                    <p>{recipe.last_updated.date}</p>
                                    <p>{recipe.cogs}</p>
                                    <p>{recipe.cost_price}</p>
                                    <p>{recipe.sale_price}</p>
                                    <p>{recipe.gross_margin}</p>
                                </div>
                                <hr></hr>
                            </div>
                        
                        ) 
                    }) :
                        null
                )
            }
        </>

    )
}
