import React, {useState, useEffect, useRef} from 'react'

export default function IsIncorrect() {

    const [isIncorrectData, setIsIncorrectData] = useState([])
    const isMounted = useRef(false)


    async function getIsIncorrectRecipes(){
        try{
            const data = await fetch('https://beta.eagleowl.in/api/v1/mock/organization/18/outlet/18/recipe/recipes/?page=1')
            const json = await data.json()
            setIsIncorrectData(json.results)
        }
        catch(err){
            console.log(err)
        }
        
    }
    
    useEffect(()=>{
        getIsIncorrectRecipes()
    },[])

    useEffect(()=>{
        if(isMounted.current){
            console.log(isIncorrectData)
        }
        else{
            isMounted.current = true
        }
    },[isIncorrectData])

    if(isMounted.current){
        return(
            <div>
                hey there
            </div>
        )
    }
    else{
        return(
            <div>
                Loading...
            </div>
        )
    }
}
