import React, {useState,useEffect, useRef} from 'react'

export default function AllRecipes() {

    const [recipeData, setRecipeData] = useState([])
    const isMounted = useRef(false)

    async function getAllRecipes(){
        try{
            const data = await fetch('https://beta.eagleowl.in/api/v1/mock/organization/18/outlet/18/recipe/recipes/?page=1')
            const json = await data.json()
            setRecipeData(json.results)
        }
        catch(err){
            console.log(err)
        }
        
    }
    
    useEffect(()=>{
        getAllRecipes()
    },[])

    useEffect(()=>{
        if(isMounted.current){
            console.log(recipeData)
        }
        else{
            isMounted.current = true
        }
    },[recipeData])

    return(
        <>
            {
                (isMounted.current ? recipeData.map( recipe => {
                    return(
                        <div key={recipe.id}>
                            <p>{recipe.name}</p>
                            {/* <p>{recipe.last_updated}</p> */}
                            {/* <p>{recipe.cogs}</p>
                            <p>{recipe.cost_price}</p>
                            <p>{recipe.sale_price}</p>
                            <p>{recipe.gross_margin}</p> */}
                        </div>
                        ) 
                    }) :
                        null
                )
            }
        </>

    )
}
