import React from 'react'

export default function IsUntagged() {


    const [untaggedData, setUntaggedData] = useState([])
    const isMounted = useRef(false)


    async function getUntaggedRecipes(){
        try{
            const data = await fetch('https://beta.eagleowl.in/api/v1/mock/organization/18/outlet/18/recipe/recipes/?page=1&is_untagged=true')
            const json = await data.json()
            setUntaggedData(json.results)
        }
        catch(err){
            console.log(err)
        }
        
    }
    
    useEffect(()=>{
        getUntaggedRecipes()
    },[])

    useEffect(()=>{
        if(isMounted.current){
            console.log(untaggedData)
        }
        else{
            isMounted.current = true
        }
    },[untaggedData])

    return(
        <>
            {
                (isMounted.current ? ( untaggedData.length === 0 ? <NothingToDisplay /> : untaggedData.map( recipe => {
                    return(
                            <div  key={recipe.id}>
                                <div>
                                    <p>{recipe.name}</p>
                                    <p>{recipe.last_updated.date}</p>
                                    <p>{recipe.cogs}</p>
                                    <p>{recipe.cost_price}</p>
                                    <p>{recipe.sale_price}</p>
                                    <p>{recipe.gross_margin}</p>
                                </div>
                                <hr></hr>
                            </div>
                        
                        ) 
                    })) : null)
            }
        </>

    )

}
