export default function IsIncorrect() {

    const [incorrectData, setIncorrectData] = useState([])
    const isMounted = useRef(false)


    async function getIncorrectRecipes(){
        try{
            const data = await fetch('https://beta.eagleowl.in/api/v1/mock/organization/18/outlet/18/recipe/recipes/?page=1')
            const json = await data.json()
            setIncorrectData(json.results)
        }
        catch(err){
            console.log(err)
        }
        
    }
    
    useEffect(()=>{
        getIncorrectRecipes()
    },[])

