import React, {useState, useEffect, useRef} from 'react'
import NothingToDisplay from './NothingToDisplay'

export default function TopFluctuatingRecipes() {


    const [topFluctuatingData, setTopFluctuatingData] = useState([])
    const isMounted = useRef(false)


    async function getTopFluctuatingRecipes(){
        try{
            const data = await fetch('https://beta.eagleowl.in/api/v1/mock/organization/18/outlet/18/recipe/margin-group/?order=bottom')
            const json = await data.json()
            console.log(json)
            setTopFluctuatingData(json.results)
        }
        catch(err){
            console.log(err)
        }
        
    }
    
    useEffect(()=>{
        getTopFluctuatingRecipes()
    },[])

    useEffect(()=>{
        if(isMounted.current){
            console.log(topFluctuatingData)
        }
        else{
            isMounted.current = true
        }
    },[topFluctuatingData])

    return(
        <>
            {
                (isMounted.current ? ( topFluctuatingData.length === 0 ? <NothingToDisplay /> : topFluctuatingData.map( data => {
                    console.log(data)
                    return(
                            <div>
                                <div>
                                    <p>{data.name}</p>
                                    <p>{data.margin}</p>
                                </div>
                                <hr></hr>
                            </div>
                        
                        ) 
                    })) : null)
            }
        </>

    )

}
