import React, {useState, useEffect, useRef} from 'react'
import NothingToDisplay from './NothingToDisplay'
import { Checkbox, withStyles, makeStyles, Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Paper } from '@material-ui/core'

const useStyles = makeStyles(theme => (
    {
            headingColor : {
                backgroundColor :'#94b3f6',
            },
            tableRowHover: {
                "&:hover": {
                        backgroundColor: "#dad9df !important",
                    }
            }
    }));


const StyledTableRow = withStyles((theme) => ({
    root: {
      '&:nth-of-type(odd)': {
        backgroundColor: '#f4f3f9',
      },
    },
  }))(TableRow);

export default function IsDisabled() {

    const classes = useStyles()
    const [checked, setChecked] = useState(false)
    const [selected , setSelected] = useState()
    const [disabledData, setDisabledData] = useState([])
    const isMounted = useRef(false)

    // isSelected = id => selected.indexOf(id) !== -1;

    async function getDisabledRecipes(){
        try{
            const data = await fetch('https://beta.eagleowl.in/api/v1/mock/organization/18/outlet/18/recipe/recipes/?page=1&is_disabled=true')
            const json = await data.json()
            setDisabledData(json.results)
        }
        catch(err){
            console.log(err)
        }
        
    }
    
    useEffect(()=>{
        getDisabledRecipes()
    },[])

    useEffect(()=>{
        if(isMounted.current){
            console.log(disabledData)
        }
        else{
            isMounted.current = true
        }
    },[disabledData])

    // handleCheckboxClick = (event, id) => {
    //     event.stopPropagation();
    //     console.log("checkbox select");
    //     const selectedIndex = selected.indexOf(id);
    //     let newSelected = [];
    
    //     if (selectedIndex === -1) {
    //       newSelected = newSelected.concat(selected, id);
    //     } else if (selectedIndex === 0) {
    //       newSelected = newSelected.concat(selected.slice(1));
    //     } else if (selectedIndex === selected.length - 1) {
    //       newSelected = newSelected.concat(selected.slice(0, -1));
    //     } else if (selectedIndex > 0) {
    //       newSelected = newSelected.concat(
    //         selected.slice(0, selectedIndex),
    //         selected.slice(selectedIndex + 1)
    //       );
    //     }
    // }
    const handleChange = (event) => {
        setChecked(event.target.checked);
      };

    return(
        <>
            {
                (isMounted.current ? ( disabledData.length === 0 ? <NothingToDisplay /> : 
                    <TableContainer component = {Paper}>
                        <Table aria-label = "disabled-recipes">
                            <TableHead>
                                <TableRow className = {classes.headingColor}>
                                    <TableCell className="selectCheckbox" padding="checkbox">
                                    <Checkbox
                                        onChange={handleChange}
                                        color="primary"
                                        inputProps={{ 'aria-label': 'secondary checkbox' }}
                                    />
                                    </TableCell>
                                    <TableCell>NAME</TableCell>
                                    <TableCell>LAST UPDATED</TableCell>
                                    <TableCell>COGS</TableCell>
                                    <TableCell>COST PRICE</TableCell>
                                    <TableCell>SALE PRICE</TableCell>
                                    <TableCell>GROSS MARGIN</TableCell>
                                </TableRow>
                            </TableHead>
                            <TableBody>
                                {
                                    disabledData.map( recipe => (
                                        <StyledTableRow key={recipe.id} hover className={classes.tableRowHover}>
                                            <TableCell padding="checkbox">
                                                <Checkbox
                                                checked={isItemSelected}
                                                inputProps={{ 'aria-labelledby': recipe.id }}
                                                />
                                            </TableCell>
                                            <TableCell>{recipe.name}</TableCell>
                                            <TableCell>{recipe.last_updated.date}</TableCell>
                                            <TableCell>{recipe.cogs}</TableCell>
                                            <TableCell>{recipe.cost_price}</TableCell>
                                            <TableCell>{recipe.sale_price}</TableCell>
                                            <TableCell>{recipe.gross_margin}</TableCell>
                                        </StyledTableRow>
                                    ))
                                }
                            </TableBody>
                        </Table>
                    </TableContainer>
                ) : null)
            }
        </>

    )


}
